<script type="text/javascript">
(function(){
	var open  = document.getElementById('zotop_trace_open');
	var close = document.getElementById('zotop_trace_close');
	var tabs  = document.getElementById('zotop_trace_tabs');
	var nav   = document.getElementById('zotop_trace_tabs_nav').getElementsByTagName('span');
	var box   = document.getElementById('zotop_trace_tabs_box').getElementsByTagName('dl');
	var trace = document.cookie.match(/zotop_trace_show=(\d)/);
		trace = (trace == null) ? 0 : parseInt(trace[1]);
	var tab   = document.cookie.match(/zotop_trace_tab=(\d)/);
		tab = (tab == null) ? 0 : parseInt(tab[1]);

	open.onclick = function(){
		tabs.style.display = 'block';
		open.style.display = 'none';
		document.cookie = 'zotop_trace_show=1';
	}

	close.onclick = function(){
		tabs.style.display = 'none';
		open.style.display = 'block';
		document.cookie = 'zotop_trace_show=0';
	}	

	for (var i = 0;i <= nav.length-1;i++ ){
	     nav[i].onclick = function(){	            
            for (var i = 0;i <= nav.length-1;i++ ){
                 if (nav[i] == this){
					nav[i].className = 'on';
					box[i].className = 'on';
					document.cookie  = 'zotop_trace_tab='+i;
                 }else{
					nav[i].className = '';
					box[i].className = '';
                 }
           }
	    }
	}

	trace==1 && open.click();

	nav[tab] && nav[tab].click();

})();
</script>